    public double purchase_price, discounted_amount, final_amount;
    public final double threshhold = 10.0;
    public final double discount = 0.10;
    
    public double round(double toround){
        toround = Math.round(toround*100.0)/100.0;
        return toround;
    }
    
    public void refresh(boolean isreset){
        if(!isreset){
            String temp = purchaseprice.getText();
            purchase_price = Double.parseDouble(temp);
            discounted_amount = 0.00;
        }
        if(purchase_price > threshhold){
            discounted_amount = purchase_price*discount;
        }
        final_amount = purchase_price-discounted_amount;
        
        purchase_price = round(purchase_price);
        discounted_amount = round(discounted_amount);
        final_amount = round(final_amount);
        
        discount_final_label.setText("$" + discounted_amount);
        final_final_label.setText("$" + final_amount);
    }
    
