    private void reset_bActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reset_bActionPerformed
       purchase_price = 0;
       discounted_amount = 0;
       final_amount = 0; 
       
       refresh(true);
       
       purchaseprice.setText("666");
    }//GEN-LAST:event_reset_bActionPerformed

